html.script:
var browserSampleNum=1,sampleCount=2;
//Load media samples into the browser.
setTimeout(function(){loadSamples();},1000);     

function loadSamples()
{   var resultArray;
    if(browserSampleNum <= sampleCount)
    {
        var request = new XMLHttpRequest();
        request.open('GET', 'sample'+browserSampleNum+'.x', true);
        request.send();
        request.onreadystatechange = function() {
            if (this.readyState == 4 && this.status == 200) {
                var scriptContentsWithTime = this.responseText;
                var scriptContents;
                var components = scriptContentsWithTime.split('\n');
                var time = components.shift();
                console.log(time)
                scriptContents = components.join('\n');    
                var theScript = document.createElement('script');
                theScript.innerHTML = scriptContents;
                document.body.appendChild(theScript);
                browserSampleNum+=1;
                setTimeout(function(){loadSamples();},time*1000); //Next sample loading after decode duration. 
            }
        };

    }
}
